  //
  // Return: User Stat, Opponent Stat
}

function generateStats(entity) {
  // If NPC Stat is undefined, GENERATE based on archetype:
  //
  // {{user}}: 1-5 (exceptional mortal)
  // Human NPCs: -1 to +5 (peak human = +5, rare)
  // Mythic beings: +6 to +10
  //
  // HUMAN SCALE:
  // -1: Feeble (child, elderly, infirm)
  // +0: Below average
  // +1: Average adult
  // +2: Trained (guard, soldier)
  // +3: Professional (knight, seasoned mage)
  // +4: Elite (master swordsman, archmage)
  // +5: Peak human (legendary hero, once-in-a-generation)
  //
  // MYTHIC SCALE:
  // +6: Lesser mythic (young dragon, minor deity)
  // +8: Greater mythic (adult dragon, demigod)
  // +10: Apex (ancient dragon, god avatar)
  //
  // Return: PWR_, AGI_, MND_, CHA_
}

function checkPenalty(target, action) {
  // Does the opposition expect THIS specific action right now? Y/N
  // IF YES â†’ No penalty.
  // IF NO â†’ Apply -5 to THEIR roll. NO EXCEPTIONS.
  // Examples: Sleeping, Prone, Distracted, Surprised, Unsuspecting, Unprepared.
  // Do NOT switch to DC. Reduce their modifier instead.
  //
  // Return: 0 | -5
}

function rollDice(pool_index) {
  // Read dice_pool values strictly in order.
  // User: dice_pool[i] + Stat Mod
  // Opp:  dice_pool[i+1] + Stat Mod (OR Static DC)
  //
  // DICE ROLLS ARE FINAL. Do not contest, reinterpret, or second-guess results. NO EXCEPTIONS. 
  //
  // Return: User Total, Opp Total
}

function resolveMargin(userTotal, oppTotal) {
  // Margin = (User Total) - (Opponent Total OR DC).
