const EVENT_RULES = {
  // WE INJECT THE MACROS HERE SO THE VALUES ARE FIXED FOR THIS TURN
  activation_val: {{roll 20}},
  outcome_val:    {{roll 20}},

  // 20% Chance of interruption.
  threshold: {
    PASSIVE: "If activation_val is 1-16 -> No Event. Continue narrative flow.",
    ACTIVE:  "If activation_val is 17-20 -> EVENT TRIGGERED. Proceed to Outcome Scale."
  }
}

// --- 2. THE SCALES (TYPE) ---
// Only accessed if EVENT_RULES.activation_val >= 17.
const OUTCOME_SCALE = {
  HOSTILE: {
    range: "1-5",
    tone: "Catastrophic to Minor Annoyance.",
    examples: "Ambush, Injury, Theft, Equipment Failure, Hostile Weather."
  },
  COMPLICATION: {
    range: "6-14",
    tone: "Neutral shift. Delays or Misunderstandings.",
    examples: "Sudden crowd, bizarre distinct observation, weather shift, harmless stranger."
  },
  BENEFICIAL: {
    range: "15-20",
    tone: "Positive. Lucky break or Miracle.",
    examples: "Found item, helpful NPC, enemy mistake, clarity."
  }
}

// --- 3. THE INJECTION (SPAWNING) ---
// Rule: Context dictates the _nature_ of the event.
const INJECTION_PROTOCOL = {
  PUBLIC: {
    logic: "High probability of Entity presence.",
    mandate: "MUST spawn a new or recurring NPC with unique name/dialogue."
  },
  ISOLATED: {
    logic: "Zero probability of Entity presence.",
    mandate: "Focus on Sensory Shifts (Sound/Smell) or Environmental Hazards (Collapse/Weather)."
  }
}

// --- 4. EXECUTION (FUNCTION) ---
function executeEventLogic(locationContext) {
  // 1. READ INJECTED VALUE: EVENT_RULES.activation_val
  // 2. CHECK: Is activation_val <= 16? 
  //    -> Return NULL (No Event).

  // 3. IF (activation_val >= 17) -> TRIGGER ACTIVE.
  //    a. READ INJECTED VALUE: EVENT_RULES.outcome_val
  //    b. Map outcome_val to OUTCOME_SCALE (Hostile/Comp/Beneficial).
  //    c. Check locationContext (Public vs Isolated).
  // 4. GENERATE PROMPT INJECTION:
  //    "Suddenly, [Event Description] interrupts the scene..."

  return { triggered: true, val: EVENT_RULES.outcome_val, detail: [Contextual_Event] };
}
